import { useState } from 'react';
import './App.css';

function App() {

  const [iconimg, setIconImg] = useState('');


  const fetchData = async()=>{

     const url = 'https://api.openweathermap.org/data/2.5/weather?q=Patna&appid=2d4a63ea0bfee20697bf482725ccf5b0';
     const data = await fetch(url);
     const result = await data.json();
     console.log(result);

     const img_icon =  result['weather'][0]['icon'] ;
     const final_img_icon = `https://openweathermap.org/img/wn/${img_icon}.png` ;
     setIconImg(final_img_icon);

  }
  fetchData();




  return (
    <>


    <div  style={{width:'500px',margin:'auto',paddingTop:'20px'}}>
     <p>Enter your location</p>
     <input className="container col-md-6 col-lg-6" type="search" placeholder="Search" aria-label="Search" />

     <div class="card">
      <div style={{backgroundColor:'#000',textAlign:'center'}}>
      <img src={iconimg} alt='This is weather icon'style={{width:'50px',height:'50px'}} />
      </div> 
      <div class="card-body">
        <h5 class="card-title">Card title</h5>
        <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
        <a href="#" class="btn btn-primary">Go somewhere</a>
      </div>
    </div>


    </div>


     </>
  );
}

export default App;
